mapper.select:
SELECT o.orders_id ordersId, o.flow_number flowNumber, o.express_number expressNumber, o.goods_price goodsPrice, o.express_price expressPrice, o.orders_name ordersName, o.orders_price ordersPrice, o.orders_count ordersCount, o.status status, o.integral_status integralStatus, o.coupons_status couponsStatus, o.create_time createTime, oc.consignee_name consigneeName, p.pay_flow_number payFlowNumber FROM t_shop_orders o INNER JOIN t_shop_orders_consignee oc ON o.orders_id = oc.orders_id LEFT JOIN t_shop_pay p ON o.orders_id = p.orders_id
o.flow_number LIKE CONCAT('%',#{flowNumber},'%')
o.express_number LIKE CONCAT('%',#{expressNumber},'%')
p.pay_flow_number LIKE CONCAT('%',#{payFlowNumber},'%')
o.orders_price LIKE CONCAT('%',#{ordersPrice},'%')
o.status = #{status}
o.integral_status = #{integralStatus}
o.coupons_status = #{couponsStatus}
o.orders_name LIKE CONCAT('%',#{ordersName},'%')
oc.consignee_name LIKE CONCAT('%',#{consigneeName},'%')
AND DATE_FORMAT(o.create_time,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
ORDER BY o.create_time DESC
