mapper.select:
SELECT r.return_orders_id returnOrdersId, r.return_flow_number returnFlowNumber, r.return_orders_price returnOrdersPrice, r.return_type returnType, r.goods_type goodsType, r.status status, r.return_remark returnRemark, r.check_fail_remark checkFailRemark, r.create_time createTime, r.check_time checkTime, r.check_fail_time checkFailTime, g.goods_name goodsName, u.user_nick_name userNickName, o.flow_number flowNumber, au.admin_user_name checkUserName FROM t_shop_return_orders r INNER JOIN t_shop_goods g ON r.goods_id = g.goods_id INNER JOIN t_web_user u ON r.user_id = u.user_id INNER JOIN t_shop_orders o ON r.orders_id = o.orders_id LEFT JOIN t_admin_user au ON r.check_user_id = au.admin_user_id
r.return_flow_number LIKE CONCAT('%',#{returnFlowNumber},'%')
r.return_type = #{returnType}
r.goods_type = #{goodsType}
r.status = #{status}
g.goods_name LIKE CONCAT('%',#{goodsName},'%')
u.user_nick_name LIKE CONCAT('%',#{userNickName},'%')
o.flow_number LIKE CONCAT('%',#{flowNumber},'%')
AND DATE_FORMAT(r.create_time,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
ORDER BY r.create_time DESC
